import * as ErrorCodes from './ErrorCodes';
import { IGlimpseError } from './IGlimpseError';

export function noContext(): IGlimpseError {
    return {
        errorCode: ErrorCodes.NO_CONTEXT,
        message: 'Glimpse was unable to retrieve the context associated with a request at location %s'
    };
}

export function unexpectedContext(): IGlimpseError {
    return {
        errorCode: ErrorCodes.UNEXPECTED_CONTEXT_VALUE,
        message: 'Glimpse unexpected context value at location %s.  Expecting context ID %s.  Actual context ID is %s'
    };
}

export function packageRequiredBeforeInit(packageName: string) {
    return {
        errorCode: ErrorCodes.PACKAGE_REQUIRED_BEFORE_INIT,
        message: `The package '${packageName}' was imported before Glimpse was initialized.  Glimpse may not capture data related to that package.`
    };
}
