import { IncomingMessage, ServerResponse } from 'http';

import { IAgent } from '../IAgent';

export interface IServerRequestInspector {
    init(agent: IAgent): void;
    requestStart(req: IncomingMessage, res: ServerResponse);
    /* tslint:disable:no-any */
    requestEnd(req: IncomingMessage, res: ServerResponse, content: Array<any>, size: number): void;
    responseEnd(req: IncomingMessage, res: ServerResponse, content: Array<any>, size: number): void;
    /* tslint:disable:no-any */
}
