'use strict';

import { IResource } from './IResource';

import express = require('express');
import path = require('path');

export class Resource implements IResource {
    public name = 'client';
    public uriTemplate = '?baseUrl=/glimpse/client&hash={hash}{&requestId,follow,metadataUri}';
    public templateName = 'clientScriptTemplate';
    public type = 'client';
    public invoke = express.static(path.join(__dirname, './embedded/client'));
}
